class PredictiveServiceEndpoint(object):
    """
    Predictive Service local endpoint. The endpoint contains the S3 path and the name.
    """
    def __init__(self, name, s3_path, aws_credentials):
        self.name = name   # name of predictive service
        self.s3_path = s3_path # s3_path of the predictive service
        self.aws_credentials = aws_credentials # aws credentials
